import json
import vk_api
from vk_api.bot_longpoll import VkBotLongPoll, VkBotEventType
import random
from datetime import datetime

import requests
from bs4 import BeautifulSoup

from config import TOKEN, CLUB_ID

def main():
    vk_session = vk_api.VkApi(
        token=TOKEN)

    longpoll = VkBotLongPoll(vk_session, CLUB_ID)
    for event in longpoll.listen():
        if event.type == VkBotEventType.MESSAGE_NEW:
            user_text = event.obj.message['text']

            try:
                url = 'https://ru.wikipedia.org/w/api.php'
                params = {
                    'action': 'parse',
                    'page': user_text,
                    'format': 'json',
                    'prop': 'text',
                    'redirects': ''
                }

                response = requests.get(url, params=params)
                data = response.json()
                print(data)

                raw_html = data['parse']['text']['*']
                soup = BeautifulSoup(raw_html, 'html.parser')
                soup.find_all('p')
                text = ''

                for p in soup.find_all('p'):
                    text += p.text

                text += f'\nЧитать далее: https://ru.wikipedia.org/wiki/{data["parse"]["title"]}'
            except:
                text = 'По вашему запросу нечего не нашлось'

            vk = vk_session.get_api()
            vk.messages.send(user_id=event.obj.message['from_id'],
                             message=text[:1000],
                             random_id=random.randint(0, 2 ** 64))


if __name__ == '__main__':
    main()


